import { SelectService } from './select.service'
export default {
  data() {
    return {
      show: false,
      coachIds: [],
      keyword: '',
      size: 50,
      list: []
    }
  },
  serviceInject() {
    return {
      selectService: SelectService
    }
  },
  props: {
    selected: {
      type: Array,
      default() {
        return []
      }
    },
    shopIds: {
      type: Array,
      default() {
        return []
      }
    },
    type: {
      type: String,
      default: ''
    }
  },
  created() {
    this.coachIds = this.selected
    this.search()
  },
  methods: {
    onChange(coachIds) {
      this.coachIds = coachIds
      this.onSearch('')
    },
    onSearch(keyword) {
      this.keyword = keyword
      this.search('')
    },
    search() {
      let para = {
        shop_ids: this.shopIds,
        size: this.size,
        keyword: this.keyword
      }
      if (this.type === 'smallCourse') {
        para.course_type = 5
      }
      this.selectService.getCoachSelect(para).subscribe(res => {
        this.list = res.coaches
      })
    },
    onConfirmSelect() {
      this.show = false
      this.$emit('change', this.coachIds)
    },
    onCancelSelect() {
      this.show = false
    },
    onFocus() {
      this.onSearch('')
    },
    onBlur() {
      this.keyword = ''
    }
  }
}
