import BizForm from './component#/form'
import AddCourseFixTable from './component#/add-course-fix-table'
import { AddFixPackageService } from './add-fix-package.service'
import { isEmpty } from 'lodash-es'

export default {
  name: 'AddFixPackage',
  components: {
    BizForm,
    AddCourseFixTable
  },
  serviceInject() {
    return {
      service: AddFixPackageService
    }
  },
  watch: {
    rangeCourse: {
      handler: function(newValue) {
        this.getValidateCourseRange()
      },
      deep: true
    }
  },
  data() {
    return {
      rangeCourse: {},
      useShopInfo: {},
      isRestCourseRange: true,
      isCourseRange: true,
      styleDanger: '',
      validateCourseRange: ''
    }
  },
  methods: {
    onRestUseShop() {
      this.isRestCourseRange = false
      let timer = setTimeout(() => {
        this.isRestCourseRange = true
        clearTimeout(timer)
      })
    },
    onUseShop(useShopInfo) {
      this.useShopInfo = useShopInfo
    },
    onCourseRange(rangeCourse) {
      this.rangeCourse = { ...rangeCourse }
    },
    getValidateCourseRange() {
      if (isEmpty(this.rangeCourse)) {
        this.isCourseRange = false
        this.validateCourseRange = '请新增课程'
        this.styleDanger = 'border: 1px solid red'
        return true
      } else {
        this.isCourseRange = true
        this.styleDanger = ''
        this.validateCourseRange = ''
      }
    },
    onSubmit(form) {
      if (this.getValidateCourseRange()) return
      this.service.add({ ...this.rangeCourse, ...form }).subscribe(res => {
        this.$router.push({
          name: 'brand-product-package-list'
        })
      })
    },
    onSaveAndUp(form) {
      if (this.getValidateCourseRange()) return
      this.service.addAndUp({ ...this.rangeCourse, ...form }).subscribe(res => {
        this.$router.push({
          name: 'brand-product-package-list'
        })
      })
    }
  }
}
