import { UserService } from '@/services/user.service'
import { GatheringTipService } from './gathering-tip.service'
export default {
  name: 'ModalSoldDealGatheringTip',
  bem: {
    gatheringTip: 'modal-sold-deal-gathering-tip'
  },
  data() {
    return {
      show: false
    }
  },
  serviceInject() {
    return {
      userService: UserService,
      gatheringTipService: GatheringTipService
    }
  },
  rxState() {
    return {
      isBrandStudio: this.userService.isBrandStudio$,
      auth: this.gatheringTipService.auth$
    }
  },
  props: {
    order_id: Number, // 订单id
    type: String,
    member_id: Number, // 会员id
    message: String,
    needPay: Boolean, // true 需要支付按钮 false 不需要
    isFamilyCard: Boolean, // true 需要展示变更成员按钮 false 不需要
    needContract: {
      type: Boolean,
      default: true
    },
    needTicket: {
      type: Boolean,
      default: true
    }
  },
  methods: {
    print() {
      this.show = false
      this.$emit('success', {
        orderId: this.order_id,
        type: 'Print'
      })
    },
    printOrder() {
      this.show = false
      this.$emit('success', {
        orderId: this.order_id,
        type: 'PrintOrder'
      })
    },
    viewOrder() {
      this.show = false
      this.$emit('success', {
        orderId: this.order_id,
        type: 'ViewOrder'
      })
    },
    goPay(e) {
      this.show = false
      this.$emit('success', {
        orderId: this.order_id,
        type: 'Pay'
      })
    },
    // 取消
    onCancel() {
      this.show = false
      this.$emit('success', {
        orderId: this.order_id,
        type: 'cancel'
      })
    },
    // 变更成员
    changeMember() {
      this.show = false
      this.$emit('success', {
        orderId: this.order_id,
        type: 'ChangeMember'
      })
    }
  }
}
