import { ruleOptions } from './area.config'
import { RuleService } from './rule.service'
import { PAY_RULE } from '@/constants/setting/hardware'

export default {
  serviceInject() {
    return {
      ruleService: RuleService
    }
  },
  rxState() {
    return {
      rule: this.ruleService.rule$
    }
  },
  bem: {
    bPage: 'modal-shop-rule'
  },
  props: {
    isEdit: {
      type: Boolean,
      default: false
    },
    value: {
      type: Number
    },
    type: {
      type: Number
    }
  },
  data() {
    const form = this.$stForm.create()
    const decorators = form.decorators(ruleOptions)
    return {
      show: false,
      form,
      decorators,
      charge_type: '',
      charge_value: '',
      PAY_RULE
    }
  },
  created() {
    this.charge_value = this.value || 2
    this.charge_type = this.type
  },
  computed: {},
  methods: {
    postGlobalSetting() {
      return this.ruleService
        .postGlobalSetting({
          charge_type: this.charge_type,
          charge_value: this.charge_value
        })
        .subscribe(res => {
          this.show = false
          this.$emit('success')
        })
    }
  }
}
