import DiViewDirective from './directives/di-view'
import refs from './refs'
export default {
  install(Vue) {
    Vue.component('di-view', {
      props: {
        name: {
          type: String
        }
      },
      render(h) {
        return h('div')
      },
      mounted() {
        refs[this.name] = this.$el
      }
    })
    Vue.directive(DiViewDirective.name, DiViewDirective)
  }
}
