export const isArray = s => Array.isArray(s)

export const isPlainObject = s =>
  typeof s === 'object' && !isArray(s) && s !== null

export const isFunction = s => typeof s === 'function'

export const isString = s => typeof s === 'string'

export const set = (obj, path, value) => {
  if (!obj) {
    return
  }
  if (!isPlainObject(obj)) {
    return
  }
  if (!path) {
    return
  }
  if (!~path.indexOf('.')) {
    obj[path] = value
  } else {
    const keys = path.split('.')
    const k1 = keys[0]
    const k2 = keys[1]
    if (!obj[k1]) {
      obj[k1] = {}
    }

    obj[k1][k2] = value
  }
}
